import { Component } from '@angular/core';
import { Product } from '../../models/Product';

@Component({
  selector: 'app-home',
  templateUrl: './home.component.html',
  styleUrls: ['./home.component.scss']
})
export class HomeComponent {
  products: Product[] | undefined;

    responsiveOptions: any[] | undefined;

    constructor() {}

    ngOnInit() {
        this.products = [{
            id: '1000',
            code: 'f230fh0g3',
            name: 'Bamboo Watch',
            description: 'Product Description',
            image: 'bamboo-watch.jpg',
            price: 65,
            category: 'Accessories',
            quantity: 24,
            inventoryStatus: 'INSTOCK',
            rating: 5

        }, {
          id: '1000',
          code: 'f230fh0g3',
          name: 'Bamboo Watch',
          description: 'Product Description',
          image: 'bamboo-watch.jpg',
          price: 65,
          category: 'Accessories',
          quantity: 24,
          inventoryStatus: 'INSTOCK',
          rating: 5

      }

      ]


      this.responsiveOptions = [
        {
            breakpoint: '1400px',
            numVisible: 1,
            numScroll: 1
        },
        {
            breakpoint: '2200px',
            numVisible: 1,
            numScroll: 1
        },
        {
            breakpoint: '2000px',
            numVisible: 1,
            numScroll: 1
        }
    ];

    }
}
<div class="flex flex-column background-div  align-items-center">
  <div
    class="flex justify-content-center align-items-center w-10 h-30rem border-gray-500 border-right-1 border-left-1 shadow-2   border-round-md mt-5"
    style="background-color: #191919;">

    <div class="flex justify-center w-10 ">
      <p-carousel [value]="products" [circular]="true" styleClass="w-full bg-green-600" autoplayInterval="3000">
        <ng-template let-product pTemplate="item">
          <div
            class="border-2 w-full sm:w-1/2 md:w-1/3 lg:w-1/4 xl:w-1/5 h-auto bg-red-500 flex items-center justify-center">
            <img [src]="product.image" alt="Product Image" class="max-w-full max-h-full object-contain">
          </div>
        </ng-template>
      </p-carousel>

    </div>



  </div>
  <div
    class="flex justify-content-center align-items-center w-10 h-10rem border-gray-500 border-right-1 border-left-1 shadow-2  border-round-md mt-2"
    style="background-color: #191919;">


    <p-button styleClass="bg-red-500 hover:bg-red-600" label="Check" icon="pi pi-check"></p-button>
    <p-button styleClass="bg-green-500 hover:bg-green-600 border-green-500" label="Check" icon="pi pi-check"></p-button>


  </div>

</div>
